package org.bgtrack.auth;import org.apache.shiro.session.Session;public class ShiroLogOutAction extends ShiroBaseAction {	private static final long serialVersionUID = 1L;	public String execute() {		if (isAuthenticated()) {						Session session = this.shiroUser.getSession();			session.removeAttribute("csrfToken");						getShiroUser().logout();		}		return "success";	}}